// Parser para o output do agente Strategic Hook (JSON format)
const inputData = $input.first().json;

try {
  // 1. EXTRA√á√ÉO DA STRING JSON
  let jsonString = '';

  if (Array.isArray(inputData) && inputData[0]?.output) {
    jsonString = inputData[0].output;
  } else if (inputData?.output) {
    jsonString = inputData.output;
  } else {
    throw new Error("Campo 'output' n√£o encontrado no input");
  }

  // 2. LIMPEZA: Remove markdown backticks se existirem
  jsonString = jsonString
    .replace(/```json\n?/g, '')
    .replace(/```\n?/g, '')
    .trim();

  // 3. PARSE DO JSON
  const data = JSON.parse(jsonString);

  // 4. VALIDA√á√ÉO
  if (!data?.StrategicHookDirective) {
    throw new Error('JSON n√£o cont√©m StrategicHookDirective');
  }

  const directive = data.StrategicHookDirective;

  // 5. REESTRUTURA√á√ÉO DOS DADOS (foco em t√≠tulos)
  const cleanOutput = {
    title: directive.approved_title.text,
    charCount: directive.approved_title.char_count,

    analysis: {
      emotional: directive.approved_title.analysis.emotional_core_detected,
      rationale: directive.approved_title.analysis.selection_rationale,
      cliffhanger: directive.approved_title.analysis.cliffhanger_analysis
    },

    alternatives: directive.alternative_titles.map(alt => ({
      text: alt.text,
      chars: alt.char_count,
      emotion: alt.emotional_core
    })),

    archetypes: directive.approved_title.archetype_mapping.map(m => ({
      from: m.from,
      to: m.to,
      why: m.rationale
    })),

    original: {
      title: directive.benchmark_deconstruction.original_title,
      formula: directive.benchmark_deconstruction.decoded_viral_formula,
      core: directive.benchmark_deconstruction.decoded_emotional_core
    }
  };

  return cleanOutput;

} catch (error) {
  return {
    error: true,
    message: error.message,
    raw_input: inputData
  };
}

// Pega os itens anteriores
const titleData = $input.all()[0].json.data;
const alternativesData = $input.all()[1].json.data;

// Parse das alternativas (texto ‚Üí array)
const alternativesLines = alternativesData.split('\n').filter(line => line.trim());
const alternatives = alternativesLines.map(line => {
  // Extrai o texto entre as aspas
  const match = line.match(/^\d+\.\s*"(.+)"\s*\(\d+\/\d+\)$/);
  return {
    text: match ? match[1] : line.replace(/^\d+\.\s*/, '').replace(/\s*\(\d+\/\d+\)$/, '')
  };
});

return [{
  json: {
    title: titleData,
    alternatives: alternatives,
    analysis: {
      emotional: "Shared Outrage",  // Pegar do payload se dispon√≠vel
      rationale: "7/7 Fidelity Score"
    },
    original: {
      formula: "[Sacred family bond] + [Sacred holiday] + [Specific money] + [Third party revelation]"
    }
  }
}];

